import './Nav.css'

function Nav() {

    useEffect(() => {
        window.removeEventListener("scroll",() => {
            if (window.scrollY > 100) {
                // handleShow(true);
            } else handleShow(false);
        });
      return () => {
        window.removeEventListener("scroll");
      }
    }, [])
    


  return (
    <div className="nav">
        <img 
        className="nav__logo" 
        src="https://upload.wikimedia.org/wikipedia/commons/3/34/Red_star.svg" 
        alt="Logo"></img>

        <img 
        className="nav__avatar" 
        src="https://www.foliomag.com/wp-content/uploads/2016/12/Karl_Marx_monochromatic.svg_.png" 
        alt="Logo"></img>
    </div>
  )
}

export default Nav
