import React, { useState } from 'react';
import './App.css';
import htmlContent from './Vplayer.html';
const film_url = "https://www.2embed.ru/embed/tmdb/movie?id="


function Vplayer() {
    const [movies] = useState([]);
    return (
        
        <div className="film">
                {movies.map((movie) => (
                <iframe id="bestIframeEver" title='video' src={`${film_url}${movie.id}`} width="25%" height="160"></iframe>
                ))}
        </div>
        
    );
  }
  






function html() {
  return (
      <div dangerouslySetInnerHTML={ {__html: htmlContent} } />
  );
}


export default {Vplayer, html};
