import React, { useState, useEffect } from 'react';
import axios from './axios';
import './Row.css';


const base_url = "https://image.tmdb.org/t/p/original/"
const film_url = "https://www.2embed.ru/embed/tmdb/movie?id="

    function Row({ title, fetchUrl, isLargeRow }) {
        const [movies, setMovies] = useState([]);
        // A snippet of code which runs based on a specific condition
        useEffect(() => {
            // if [], run once the row loads, and dont run again
            async function fetchData() {
                const request = await axios.get(fetchUrl);
                setMovies(request.data.results);
                return request;

            }
            fetchData();
        }, [fetchUrl]);

        console.log(movies);
        function Iframe({}) {
            return (
                
                    <div className="film">
                        
                        <iframe id="bestIframeEver" title='video' src={`${film_url}${movie.id}`} width="15%" height="160"></iframe>

                    </div>
                
            )
        }


        return (
            <div className="row">
                <h2>{title}</h2>
                
                <div className="row__posters">
                
                {movies.map((movie) => (
                    <img 
                    key={movie.id}
                    onClick={Iframe()}
                    className={`row__poster ${isLargeRow && "row__posterlogo"}`}
                    src={`${base_url}${isLargeRow ? movie.poster_path : movie.backdrop_path
                     }`} 
                    alt={movie.id}/>
                ))}
                </div>
                
            </div>
            
        )
    }

    


    


    
    //export {Row, Iframe };

    export default Row;
