        }

        
        

        return (
            <div className="row">
                <h2>{title}</h2>
                
                <div className="row__posters">
                
                {movies.map((movie) => (
                    <img 
                    key={movie.id}
                    
                    className={`row__poster ${isLargeRow && "row__posterlogo"}`}
                    src={`${base_url}${isLargeRow ? movie.poster_path : movie.backdrop_path
                     }`} 
                    alt={movie.id}/>
                    
                ))}

                {movies.map((movie) => (

                <iframe 
                
                id="bestIframeEver" 
                title='video' 
                src={`${film_url}${movie.id}`} 
                width="25%" 
                height="160">

                </iframe>

                ))}
                </div>
                
                
            </div>
            
        )
    }

    export default Row;
